import serial.tools.list_portsimport serialdef arduinoSerial():    ports = list(serial.tools.list_ports.comports())    ArdSer = ''    for p in ports:        if "Arduino" in p[1]:            ArdSer = serial.Serial(port=p[0],baudrate=9600,parity=serial.PARITY_NONE, stopbits=serial.STOPBITS_ONE,bytesize=serial.EIGHTBITS,timeout=1)  # open serial port            return(ArdSer)